import React from 'react'
import { useState, useEffect,createContext, useContext } from 'react';
import { Link } from 'react-router-dom';
const ThemeContext = createContext();
export const useTheme = () => useContext(ThemeContext);

function Navbar() {
    const [responsiveNav, setResponsiveNav] = useState('collapse navbar-collapse');
    const [collapaseNavbar, setCollapseNavbar] = useState('navbar-toggler collapsed');
    const [themeMode, setThemeMode] = useState('light');
    const [themeIcon, setThemeIcon] = useState('bi bi-brightness-high mode');

    useEffect(() => {
        themeMode === 'light' ? setThemeIcon('bi bi-brightness-high mode') : setThemeIcon('bi bi-moon mode');
    }, [themeMode]);


    const handleResponsive = () => {
        setResponsiveNav(prevState =>
            prevState === 'collapse navbar-collapse'
                ? 'navbar-collapse collapse show'
                : 'collapse navbar-collapse'
        );
        setCollapseNavbar(prevState =>
            prevState === 'navbar-toggler'
                ? 'navbar-toggler collapsed'
                : 'navbar-toggler'
        );
    };

    const handleChangeMode = () => {
        setThemeMode(prevMode => (prevMode === 'light' ? 'dark' : 'light'));
    };

    return (
        <ThemeContext.Provider value={{ themeMode }}>
        <nav className="navbar navbar-expand-lg navbar-light bg-white py-3">
            <div className="container px-5">
                <Link className="navbar-brand" to="#"><span className="fw-bolder text-primary">Web Dev Sweta</span></Link>
                <button className={collapaseNavbar} type="button" data-bs-toggle="collapse" onClick={handleResponsive} data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="true" aria-label="Toggle navigation"><span className="navbar-toggler-icon"></span></button>
                <div className={responsiveNav} id="navbarSupportedContent">
                    <ul className="navbar-nav ms-auto mb-2 mb-lg-0 small fw-bolder d-flex justify-content-center align-items-center">
                        <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
                        <li className="nav-item"><Link className="nav-link" to="/resume">Resume</Link></li>
                        <li className="nav-item"><Link className="nav-link" to="/projects">Projects</Link></li>
                        <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
                        <li className='nav-item'><i class={themeIcon} onClick={() => handleChangeMode(themeMode)} style={{ fontSize: '30px' }}></i></li>
                    </ul>
                </div>
            </div>
        </nav>
        </ThemeContext.Provider>
    )
}

export default Navbar
